package com.Day2;

public class Exam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i <= 5; i++) {
			String val = "";
			for (int j = 0; j <= i; j++) {
				val = val + "*"+" ";
			}
			System.out.println(val);
			System.out.println();
		}

	}

}
